const { Readable }  = require('stream');

const LONG_STRING = '';

class Overflower extends Readable {
  constructor(options = {}, overflow = false) {
    options.objectMode = false;
    options.highWaterMark = 4;
    super(options);
    this.overflow = overflow;
    this.on('end', () => {
      console.log('ended');
    });
  }

  randomData() {
    return Math.random(0, 10);
  }

  // gets called on every push
  _read() {
    console.log(new Date(), this._readableState.buffer.length);
    setInterval(() => {
      const object = {
        time: new Date(),
        random: this.randomData(),
        bufferLength: this._readableState.buffer.length
      };
      const json = JSON.stringify(object);
      console.log('pushing', json);

      // Fill our read buffer
      if (!this.overflow) {
        // Make sure we dont overflow
        if (this.push()) {
          this.push(json);
        }
      } else {
        // Just push, overflow if we dont read fast enough
        this.push(json);
      }
    }, 500);
  }
}

/*
 * Readable stream that emits random data every 1 sec
 * TODO This timeout approach smells, what might be a better way?
 */
class Generator extends Readable {
  constructor(options = {}) {
    options.objectMode = true;
    super(options);
  }

  randomData() {
    return { data: Math.random(0, 10) };
  }

  _read() {
    console.log('read!');
    setTimeout(() => {
      this.push(this.randomData());
    }, 1000);
  }
}

class Sensible extends Readable {
  constructor(options = {}) {
    options.highWaterMark = 4;
    super(options);
    this._busy = false;
  }

  _read() {
    if (this._busy) {
      return;
    }
    this._busy = true;
    this.retrieveMoreData();
  }

  retrieveMoreData() {
    setTimeout(() => {
      this.doSomeWork((error, newData) => {
        if (error) {
          this.emit('error', error);
        }
        // this.push returns true if we are allowed to push into internal buffer
        const pushMore = this.push(newData);
        if (pushMore) {
          console.log(newData);
          this.retrieveMoreData();
        } else {
          this._busy = false;
        }
      });
    }, 1000);
  }

  doSomeWork(callback) {
    callback(null, JSON.stringify({
      date: new Date(),
      'G-buf': this._readableState.buffer.length
    }) + '\n');
  }
}


module.exports = { Overflower, Generator, Sensible };
